svg.script:
var svg = document.getElementsByTagName('svg')[0];
var svgns = "http://www.w3.org/2000/svg";
var SVGDocument = null;
var SVGRoot = null;

var TrueCoords = null;
var GrabPoint = null;
var BackDrop = null;
var DragTarget = null;

function InitDrag(evt) {
	SVGDocument = evt.target.ownerDocument;
	SVGRoot = SVGDocument.documentElement;
	TrueCoords = SVGRoot.createSVGPoint();
	GrabPoint = SVGRoot.createSVGPoint();
	BackDrop = SVGDocument.getElementById('BackDrop');
}

function Grab(evt) {
 var targetElement = evt.target;
 if (BackDrop != targetElement) {
	console.log(targetElement);
	if(targetElement.classList.contains("draggable")) {
        DragTarget = targetElement;
        DragTarget.parentNode.appendChild( DragTarget );
        DragTarget.setAttribute('pointer-events', 'none');
        var transMatrix = DragTarget.getCTM();
        GrabPoint.x = TrueCoords.x - Number(transMatrix.e);
        GrabPoint.y = TrueCoords.y - Number(transMatrix.f);
	} else {
		console.log("kwa?");
	}
 }
}

function Drag(evt) {
 GetTrueCoords(evt);
 if (DragTarget) {
    var newX = TrueCoords.x - GrabPoint.x;
    newY = TrueCoords.y - GrabPoint.y;
    DragTarget.setAttribute('transform', 'translate(' + newX + ',' + newY + ')');
 }
};


function Drop(evt) {
 if (DragTarget) {
    var targetElement = evt.target;
    DragTarget.setAttribute('pointer-events', 'all');
    DragTarget = null;
 }
};


function GetTrueCoords(evt) {
 var newScale = SVGRoot.currentScale;
 var translation = SVGRoot.currentTranslate;
 TrueCoords.x = (evt.clientX - translation.x)/newScale;
 TrueCoords.y = (evt.clientY - translation.y)/newScale;
};

var hide = true;

function OnClick(evt) {
	var targetElement = evt.target;
	if (BackDrop == targetElement) {
		var elements = document.getElementsByClassName('hide');
		for (var i = 0; i < elements.length; i++) {
			if(hide) {
			elements[i].setAttribute('fill', 'none');
		} else {
			elements[i].setAttribute('fill', 'black');
		}

		}
		hide = !hide;
	}
}
