svg.script:
var svg = document.getElementsByTagName('svg')[0];
var svgns = "http://www.w3.org/2000/svg";

var x = 0;
var y = 0;
var width = 20;
//var red = 'red';
//var green = 'green';
var red = '#d02b7c';
var green = '#1be84a';

function Init() {
	var off = 0;
	var j = 0;
	for (j; j < 30; j++) {
		for (var i=0; i < 20; i++) {
			var off = j%2;
			var rect = document.createElementNS(svgns, 'rect');
			rect.setAttribute('x', width*off + 2*i*width);
			rect.setAttribute('y', y+j*width);
			rect.setAttribute('width', width);
			rect.setAttribute('height', width);
			rect.setAttribute('fill', green);
			svg.appendChild(rect);

			if(i>4 && i<7) {
				var rect = document.createElementNS(svgns, 'rect');
				rect.setAttribute('x', width*off + 2*i*width);
				rect.setAttribute('y', y+j*width);
				rect.setAttribute('width', width);
				rect.setAttribute('height', width);
				rect.setAttribute('fill', red);
				rect.setAttribute('class', 'hide');
				svg.appendChild(rect);
			}


			if(i>11 && i<14) {
				var rect = document.createElementNS(svgns, 'rect');
				rect.setAttribute('x', (2*i*width - width*off) +width);
				rect.setAttribute('y', y+j*width);
				rect.setAttribute('width', width);
				rect.setAttribute('height', width);
				rect.setAttribute('fill', red);
				rect.setAttribute('class', 'hide');
				svg.appendChild(rect);
			}
		}
	}
}

var hide = true;

function OnClick() {
	var elements = document.getElementsByClassName('hide');
	for(i=0; i<elements.length; i++) {
		if(hide) {
			elements[i].setAttribute('opacity', 0);
		} else {
			elements[i].setAttribute('opacity', 1);
		}
	}
	hide = !hide;
}
