svg.script:
function startAnimation(evt) {
  const movingCircle = document.getElementById("movingCircle");
  const animation = movingCircle.getElementsByTagName("animateMotion")[0];

  // Premaknemo krog na začetno pozicijo poti
  movingCircle.setAttribute("cx", "0");
  movingCircle.setAttribute("cy", "0");

  // Začne animacijo
  animation.beginElement();
}
